import React, { useEffect, useRef } from "react";
import Header from "../Components/Header/Header";
import "./HomePage.css";

const HomePage = () => {

    const object1Ref = useRef(null);
    const object2Ref = useRef(null);

      const LineBetween = ({ from, to }) => {
        const canvasRef = useRef(null);

        useEffect(() => {
          const canvas = canvasRef.current;
          const ctx = canvas.getContext("2d");

          const rect1 = from.getBoundingClientRect();
          const rect2 = to.getBoundingClientRect();

          const x1 = rect1.right;
          const y1 = rect1.top + rect1.height / 2;
          const x2 = rect2.left;
          const y2 = rect2.top + rect2.height / 2;
    
          ctx.clearRect(0, 0, canvas.width, canvas.height);
          ctx.beginPath();
          ctx.moveTo(x1, y1);
          ctx.lineTo(x2, y2);
          ctx.strokeStyle = "#3498db";
          ctx.lineWidth = 3;
          ctx.stroke();
        }, [from, to]);

        return <canvas ref={canvasRef} className="line-canvas" />;
      };

    return (
        <>
            <Header/>
            <section className="hero-section">
                <div className="container hero-container">
                    <div className="heading">
                        <h1 className="heading-text">путешествие</h1>
                        <p className="heading-description">на красную планету</p>
                    </div>
                </div>
                <div className="container">
      <div ref={object1Ref} className="object1">
        Object 1
      </div>
      <div ref={object2Ref} className="object2">
        Object 2
      </div>
      <LineBetween from={object1Ref.current} to={object2Ref.current} />
    </div>
  );
            </section>
        </>
)
}

export default HomePage;
