import React, {useEffect} from "react";
import Header from "../Components/Header/Header";
import "./HomePage.css";
import {Link} from "react-router-dom";
import axios from "axios";

const HomePage = () => {
    let options;
    let advantages;


    useEffect(() => {
        async function fetchData() {
            try {
                const response = await axios.get("http://127.0.0.1:8000/get_data/");
                options = response.data["menu_options"];
                advantages = response.data["advantages_options"];
            } catch (error) {
                console.error("Ошибка при выполнении запроса:", error);
            }
        }

        fetchData();
    }, []);

        return (
            <>
                <Header/>
                <section className="hero-section">
                    <div className="container hero-container">
                        <div className="heading">
                            <h1 className="heading-text">путешествие</h1>
                            <p className="heading-description">на красную планету</p>
                            {window.innerWidth > 768 && (
                                <div className="heading-button-container">
                                    <button className="heading-button">Начать путешествие</button>
                                </div>
                            )}
                        </div>
                        <div className="advantages">
                            <Link to="/" className="advantages-item">
                                <p className="advantages-text">мы</p>
                                <h2 className="advantages-big-text">1</h2>
                                <p className="advantages-text">на рынке</p>
                            </Link>
                            <Link to="/" className="advantages-item">
                                <p className="advantages-text">гарантируем</p>
                                <h2 className="advantages-big-text">50%</h2>
                                <p className="advantages-text">безопасность</p>
                            </Link>
                            <Link to="/" className="advantages-item">
                                <p className="advantages-text">календарик за</p>
                                <h2 className="advantages-big-text">2001г.</h2>
                                <p className="advantages-text">в подарок</p>
                            </Link>
                            <Link to="/" className="advantages-item">
                                <p className="advantages-text">путешествие</p>
                                <h2 className="advantages-big-text">597</h2>
                                <p className="advantages-text">дней</p>
                            </Link>
                        </div>
                    </div>
                    {window.innerWidth < 425 && (
                        <div className="heading-button-container">
                            <button className="heading-button">Начать путешествие</button>
                        </div>
                    )}
                </section>
            </>
        );
    };

    export default HomePage;
